public void load( String configFileName ) {
	boolean doSave = false;
	try {
		FileReader reader = new FileReader( configFileName );
		props.load( reader );
		reader.close();
	}
	catch( FileNotFoundException e ) {
		doSave = true;
	}
	catch( IOException e ) {
		throw new CFLibRuntimeException( getClass(),
			"load",
			"IOException " + e.getMessage(),
			e );
	}
	if( doSave ) {
		save();
	}
}

