public static String xmlEncodeString( String val ) {
	StringBuffer buff = new StringBuffer();
	int len = val.length();
	for( int idx = 0; idx < len; idx ++ ) {
		char c = val.charAt( idx );
		switch( c ) {
			case '&':
				buff.append( "&amp;" );
				break;
			case '<':
				buff.append( "&lt;" );
				break;
			case '>':
				buff.append( "&gt;" );
				break;
			case '"':
				buff.append( "&quot;" );
				break;
			case '\\'':
				buff.append( "&apos;" );
				break;
			default:
				buff.append( c );
				break;
		}
	}
	return( buff.toString() );
}

